pair r = (0, 0);
pair t = dir(-55);
pair s = dir(-55 - 70);
pair p = dir(-55 + 180);
real qr = Sin(70) / Sin(40);

pair q = qr * dir(-55 - 70 + 180);

draw(r--p--q--s--t--r);

label("$R$", r, 2 * E);
label("$T$", t, SE);
label("$S$", s, SW);
label("$P$", p, W);
label("$Q$", q, E);

label("$x^\circ$", t, W + NW + NW);
