draw(Circle((0,0),1));

draw(Circle((3,0),1));

draw((0,0)--(0,1));

draw((0,0)--(-0.9,-0.47));

draw((0,0)--(0.9,-0.47));

draw((2,0)--(4,0));

draw((3,1)--(3,-1));

label("$3$",(-0.5,0.3));

label("$4$",(0.5,0.3));

label("$5$",(0,-0.5));

label("$5$",(2.6,-0.4));

label("$6$",(2.6,0.4));

label("$7$",(3.4,0.4));

label("$8$",(3.4,-0.4));

draw((0,0)--(0.2,0.8),Arrow);

draw((3,0)--(3.2,0.8),Arrow);

